// frontend\src\pages\AuthForm.js
import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
const API_URL = process.env.REACT_APP_API_URL;

function AuthForm() {
    const { isAuthenticated, login, register, logout, setIsAuthenticated } = useAuth();

    if (isAuthenticated) return <div>Вы уже авторизованы. <button onClick={logout}>Выйти</button></div>;

    const [isRegistering, setIsRegistering] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        birthDate: '',
        gender: '',
        profilePicture: null,
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isRegistering) {
                const data = await register(formData);
                console.log('Registration successful:', data);
                setIsAuthenticated(true); // Устанавливаем аутентификацию после успешной регистрации
            } else {
                console.log('Login Data:', formData);
                await login(formData);
                setIsAuthenticated(true); // Устанавливаем аутентификацию после успешного логина
            }
            if (onAuth) {
                onAuth();
            }
        } catch (error) {
            console.error('Error during form submission:', error);
        }
    };

    return (
        <div>
            <h2>{isRegistering ? 'Register' : 'Login'}</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                    />
                </div>
                <div>
                    {/* Остальные элементы формы */}
                </div>
                <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
            </form>
            <button onClick={() => setIsRegistering(!isRegistering)}>
                {isRegistering ? 'Switch to Login' : 'Switch to Register'}
            </button>
        </div>
    );
}

export default AuthForm;
