import React, { useState } from 'react';
import UsersTable from '../components/UsersTable';
import '../assets/css/people.css';

const initialUsers = [
    { id: 1, name: 'Иван Иванов', email: 'ivan@example.com', role: 'Админ' },
    { id: 2, name: 'Петр Петров', email: 'petr@example.com', role: 'Пользователь' },
    { id: 3, name: 'Сидор Сидоров', email: 'sidor@example.com', role: 'Модератор' },
];

const People = () => {
    const [users, setUsers] = useState(initialUsers);
    const [searchQuery, setSearchQuery] = useState('');

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const filteredUsers = users.filter((user) =>
        Object.values(user).some((value) =>
            value.toString().toLowerCase().includes(searchQuery.toLowerCase())
        )
    );

    return (
        <div>
            <h1>Список пользователей</h1>
            <input
                type="text"
                placeholder="Поиск..."
                value={searchQuery}
                onChange={handleSearchChange}
                className="search-input"
            />
            <UsersTable users={filteredUsers} />
        </div>
    );
};

export default People;
