import React from 'react';
import { BrowserRouter as Router, Route, Switch, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import AuthForm from './pages/AuthForm';
import Home from './pages/Home';
import Account from './pages/Account';
import People from './pages/People';
import { AuthProvider, useAuth } from './context/AuthContext';

// Компонент для управления классом контейнера и отображения содержимого
function Content() {
    const location = useLocation();
    const { isAuthenticated } = useAuth();

    // Определяем класс для контейнера в зависимости от маршрута и состояния авторизации
    const containerClass = !isAuthenticated && location.pathname !== '/login' ? 'login' : '';

    if (!isAuthenticated && location.pathname !== '/login') {
        return <AuthForm />;
    }

    return (
        <div className={`main-content ${containerClass}`}>
            <Header />
            <Sidebar />
            <div className="content">
                <Switch>
                    <Route path="/" exact component={Home} />
                    <Route path="/account" component={Account} />
                    <Route path="/people" component={People} />
                    {/* Другие маршруты */}
                </Switch>
            </div>
        </div>
    );
}
if (!isAuthenticated()) {
    const currentRoute = 'login';
}
const currentRoute = location.pathname.substring(1) || 'home';

// Основной компонент приложения
function App() {
    return (
        <Router>
            <AuthProvider>
                <div className={`app ${currentRoute}`}>
                    <Content/>
                </div>
            </AuthProvider>
        </Router>
    );
}

export default App;
