import React from 'react';
import { Route, useLocation } from 'react-router-dom';

// Функция для динамической подгрузки иконок по маршруту
const getIconByRoute = async (path) => {
    const routeName = path === '/' ? 'home' : path.replace('/', '');
    try {
        const icon = await import(`../assets/images/icons/menu/${routeName}.svg`);
        return icon.default;
    } catch (e) {
        console.error(`Icon for route "${routeName}" not found.`);
        return null;
    }
};

const RouteWithIcon = ({ path, exact, component: Component }) => {
    const location = useLocation();
    const [Icon, setIcon] = React.useState(null);

    React.useEffect(() => {
        (async () => {
            const icon = await getIconByRoute(location.pathname);
            setIcon(icon);
        })();
    }, [location.pathname]);

    return (
        <Route
            path={path}
            exact={exact}
            component={() => (
                <div>
                    {Icon && <img src={Icon} alt={path} />}
                    <Component />
                </div>
            )}
        />
    );
};

export default RouteWithIcon;
