import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const RouteHandler = ({ children }) => {
    const location = useLocation();
    const { isAuthenticated } = useAuth();

    // Определяем текущий маршрут для добавления класса
    const currentRoute = !isAuthenticated || location.pathname === '/login'
        ? 'login'
        : location.pathname.substring(1) || 'home';

    useEffect(() => {
        // Добавляем класс к элементу <html> или <body>
        document.documentElement.className = currentRoute; // Добавляем класс к <html>
        document.body.className = currentRoute; // Добавляем класс к <body>

        // Опционально: очистка классов при размонтировании компонента
        return () => {
            document.documentElement.className = '';
            document.body.className = '';
        };
    }, [currentRoute]);

    return (
        <div className={`app ${currentRoute}`}>
            {children}
        </div>
    );
};

export default RouteHandler;
