import React, { useState } from 'react';

function Menu() {
    const [isOpen, setIsOpen] = useState(false);

    const handleMenuItemClick = () => {
        setIsOpen(false); // Убираем класс open
    };

    return (
        <div className={isOpen ? 'menu open' : 'menu'}>
            <button onClick={() => setIsOpen(!isOpen)}>Toggle Menu</button>
            <ul>
                <li onClick={handleMenuItemClick}>Item 1</li>
                <li onClick={handleMenuItemClick}>Item 2</li>
                <li onClick={handleMenuItemClick}>Item 3</li>
            </ul>
        </div>
    );
}

export default Menu;
