import React, { useState, useRef } from 'react';
import { useAuth } from '../context/AuthContext';

function AuthForm() {
    const { isAuthenticated, login } = useAuth();

    if (isAuthenticated) return null;

    const [isRegistering, setIsRegistering] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        birthDate: '',
        gender: '',
        profilePicture: null,
    });

    const [dragOver, setDragOver] = useState(false);
    const fileInputRef = useRef(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (file) => {
        setFormData({
            ...formData,
            profilePicture: file,
        });
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        setDragOver(true);
    };

    const handleDragLeave = () => {
        setDragOver(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setDragOver(false);
        const file = e.dataTransfer.files[0];
        if (file && file.type.startsWith('image/')) {
            handleFileChange(file);
        }
    };

    const handleClick = () => {
        fileInputRef.current.click(); // Открываем диалоговое окно выбора файла
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isRegistering) {
            console.log('Registration Data:', formData);
        } else {
            console.log('Login Data:', formData);
            login();
        }
    };

    const toggleForm = () => {
        setIsRegistering(!isRegistering);
    };

    return (
        <div className="auth-form">
            <h2>{isRegistering ? 'Регистрация' : 'Войти'}</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        placeholder="name@example.com"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Пароль:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                {isRegistering && (
                    <>
                        <div>
                            <label>Дата рождения:</label>
                            <input
                                type="date"
                                name="birthDate"
                                value={formData.birthDate}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Ваш пол:</label>
                            <select
                                name="gender"
                                value={formData.gender}
                                onChange={handleChange}
                                required
                            >
                                <option value="" disabled>Выберите</option>
                                <option value="male">Мужчина</option>
                                <option value="female">Женщина</option>
                            </select>
                        </div>
                        <div
                            className={`drop-zone ${dragOver ? 'drag-over' : ''}`}
                            onDragOver={handleDragOver}
                            onDragLeave={handleDragLeave}
                            onDrop={handleDrop}
                            onClick={handleClick} // Добавлен обработчик клика
                        >
                            <label>Фотография:</label>
                            <input
                                type="file"
                                name="profilePicture"
                                ref={fileInputRef}
                                onChange={(e) => handleFileChange(e.target.files[0])}
                                accept="image/*"
                                style={{display: 'none'}}
                            />
                            <p className={"dragDrop"}>Перетащите сюда изображение или нажмите для выбора файла</p>
                            <p className="dragDrop">
                                {formData.profilePicture ? (
                                    <img
                                        src={URL.createObjectURL(formData.profilePicture)}
                                        alt="Preview"
                                        style={{maxWidth: '100%', maxHeight: '200px'}}
                                    />
                                ) : (
                                    "Перетащите сюда изображение или нажмите для выбора файла"
                                )}
                            </p>

                        </div>
                    </>
                )}
                <button type="submit">{isRegistering ? 'Регистрация' : 'Войти'}</button>
            </form>
            <p>
                {isRegistering ? (
                    <>Уже есть аккаунт? <a href="#" onClick={toggleForm}>Войти</a></>
                ) : (
                    <>Нет аккаунта? <a href="#" onClick={toggleForm}>Зарегистрируйтесь</a></>
                )}
            </p>
        </div>
    );
}

export default AuthForm;
