import React from 'react';
import { useLocation } from 'react-router-dom';

// Функция для динамической подгрузки иконок по маршруту
const getIconByRoute = (path) => {
    // Замените путь на ваш путь к папке с иконками
    const iconsPath = '/path/to/your/icons/';
    const routeName = path === '/' ? 'home' : path.replace('/', '');
    try {
        const Icon = require(`${iconsPath}${routeName}.svg`).default;
        return <Icon />;
    } catch (e) {
        console.error(`Icon for route "${routeName}" not found.`);
        return null;
    }
};

const RouteWithIcon = ({ path, exact, component: Component }) => {
    const location = useLocation();
    const icon = getIconByRoute(location.pathname);

    return (
        <Route
            path={path}
            exact={exact}
            component={() => (
                <div>
                    {icon}
                    <Component />
                </div>
            )}
        />
    );
};

export default RouteWithIcon;
