import React from 'react';
import { useAuth } from '../context/AuthContext'; // Импортируем хук

function AuthForm() {
    const {
        isAuthenticated,
        login
    } = useAuth();
    console.log (isAuthenticated);
    if (isAuthenticated) return null; // Если пользователь уже авторизован, не показываем форму

    const [isRegistering, setIsRegistering] = React.useState(false);
    const [formData, setFormData] = React.useState({
        email: '',
        password: '',
        birthDate: '',
        gender: '',
        profilePicture: null,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (e) => {
        setFormData({
            ...formData,
            profilePicture: e.target.files[0],
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isRegistering) {
            console.log('Registration Data:', formData);
        } else {
            console.log('Login Data:', formData);
            login(); // Устанавливаем авторизацию при успешном логине
        }
    };

    const toggleForm = () => {
        setIsRegistering(!isRegistering);
    };

    return (
        <div>
            <h2>{isRegistering ? 'Register' : 'Login'}</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        placeholder="name@example.com"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                {isRegistering && (
                    <>
                        <div>
                            <label>Birth Date:</label>
                            <input
                                type="date"
                                name="birthDate"
                                value={formData.birthDate}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Gender:</label>
                            <select
                                name="gender"
                                value={formData.gender}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Select Gender</option>
                                <option value="male">Male</option>
                                <option value="female">Female</option>
                                <option value="other">Other</option>
                            </select>
                        </div>
                        <div>
                            <label>Profile Picture:</label>
                            <input
                                type="file"
                                name="profilePicture"
                                onChange={handleFileChange}
                                accept="image/*"
                            />
                        </div>
                    </>
                )}
                <button type="submit">{isRegistering ? 'Register' : 'Login'}</button>
            </form>
            <p>
                {isRegistering ? (
                    <>Already have an account? <a href="#" onClick={toggleForm}>Login</a></>
                ) : (
                    <>Don't have an account? <a href="#" onClick={toggleForm}>Register now</a></>
                )}
            </p>
        </div>
    );
}

export default AuthForm;
