import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { getIconByRoute } from '../utils/getIconByRoute';

const RouteHandler = ({ children }) => {
    const location = useLocation();
    const [icons, setIcons] = useState({});

    useEffect(() => {
        const loadIcons = async () => {
            const routes = ['home', 'account', 'people'];
            const loadedIcons = {};
            for (const route of routes) {
                const icon = await getIconByRoute(route);
                if (icon) {
                    loadedIcons[route] = icon;
                }
            }
            setIcons(loadedIcons);
        };
        loadIcons();
    }, []);

    return (
        <>
            <nav>
                <ul>
                    <li>
                        <Link to="/" className={location.pathname === '/' ? 'active' : ''}>
                            {icons['home'] && <img src={icons['home']} alt="Home icon" />}
                            Home
                        </Link>
                    </li>
                    <li>
                        <Link to="/account" className={location.pathname === '/account' ? 'active' : ''}>
                            {icons['account'] && <img src={icons['account']} alt="Account icon" />}
                            Account
                        </Link>
                    </li>
                    <li>
                        <Link to="/people" className={location.pathname === '/people' ? 'active' : ''}>
                            {icons['people'] && <img src={icons['people']} alt="People icon" />}
                            People
                        </Link>
                    </li>
                </ul>
            </nav>
            {children}
        </>
    );
};

export default RouteHandler;
