import React from 'react';
import { NavLink } from 'react-router-dom';

const routes = [
    { path: '/', name: 'Home', exact: true },
    { path: '/account', name: 'Account' },
    { path: '/people', name: 'People' },
];

function Sidebar({ isAsideOpen }) {
    return (
        <aside className={isAsideOpen ? 'open' : ''}>
            <nav>
                <ul>
                    <li>
                        <button className="hamburger" onClick={toggleAside}>
                            â˜°
                        </button>
                    </li>
                    {routes.map((route) => (
                        <li key={route.path} className={`route-${route.name.toLowerCase()}`}>
                            <NavLink exact={route.exact} to={route.path} activeClassName="active">
                                <span>{route.name}</span>
                            </NavLink>
                        </li>
                    ))}
                </ul>
            </nav>
        </aside>
    );
}

export default Sidebar;
