import React from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const routes = [
    { path: '/', name: 'Home', exact: true },
    { path: '/account', name: 'Account' },
    { path: '/people', name: 'People' },
];

function Sidebar({ isAsideOpen, toggleAside }) {

    const { logout } = useContext(AuthContext);

    const handleLogout = (e) => {
        e.preventDefault();
        logout();
        const history = useHistory();
        history.push('/login'); // Перенаправление на страницу входа
    };

    // Функция-обработчик, которая вызывает toggleAside только если сайдбар открыт
    const handleLinkClick = () => {
        if (isAsideOpen) {
            toggleAside();
        }
    };

    return (
        <aside className={isAsideOpen ? 'open' : ''}>
            <nav>
                <ul>
                    <li className="route-open" onClick={toggleAside}></li>
                    {routes.map((route) => (
                        <li key={route.path} className={`route-${route.name.toLowerCase()}`}>
                            <NavLink
                                exact={route.exact}
                                to={route.path}
                                activeClassName="active"
                                onClick={handleLinkClick} // Используем новую функцию-обработчик
                            >
                                <span>{route.name}</span>
                            </NavLink>
                        </li>
                    ))}
                    <li><a onClick={handleLogout} href="/logout">Logout</a></li>
                </ul>
            </nav>
        </aside>
    );
}

export default Sidebar;
