import React, { createContext, useState, useContext } from 'react';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL;
const AuthContext = createContext();


export function AuthProvider({ children }) {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const login = async (formData) => {
        try {
            const response = await fetch(API_URL + '/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            if (!response.ok) {
                // Получаем текст ошибки, если он есть
                const errorData = await response.json();
                throw new Error(errorData.error || 'Произошла ошибка, попробуйте снова');
            }

            const data = await response.json();
            setIsAuthenticated(true); // Предполагаем, что успешный логин делает аутентификацию истинной
            return data;
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
            // Можно добавить дополнительную обработку ошибок или переопределение
            throw error; // Перебрасываем ошибку дальше, чтобы её можно было обработать в компоненте
        }
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, login }}>
            {children}
        </AuthContext.Provider>
    );
}


export function useAuth() {
    return useContext(AuthContext);
}
