import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const RouteHandler = ({ children }) => {
    const location = useLocation();
    const { isAuthenticated } = useAuth();

    // Определяем текущий маршрут для добавления класса
    const currentRoute = !isAuthenticated || location.pathname === '/login'
        ? 'login'
        : location.pathname.substring(1) || 'home';

    useEffect(() => {
        const rootElement = document.getElementById('root');
        const currentRoute = window.location.pathname.slice(1) || 'home';

        if (rootElement) {
            rootElement.className = `root route-${currentRoute}`; // Добавляем нужный класс к #root
        }
        // Опционально: очистка классов при размонтировании компонента
        return () => {
            document.documentElement.className = '';
            document.body.className = '';
        };
    }, [currentRoute]);

    return (
        <div className={`app`}>
            {children}
        </div>
    );
};

export default RouteHandler;
