import React, { useState } from 'react';
import UsersTable from '../components/UsersTable';
import '../assets/css/people.css';

function Home() {
    const baseUser = {
        photo: 'https://sun1-94.userapi.com/s/v1/ig2/6F6D98xB9-_xMDx59wqOx4AbxMe59NDTQG8rBvMVJxCaysx3S4V0gBzgbHos_V5e2fMwNO1YgjqMELemmP3nd-5g.jpg?quality=95&crop=0,0,482,482&as=32x32,48x48,72x72,108x108,160x160,240x240,360x360,480x480&ava=1&u=9ORr4tv6F3UUjFkDnYKwTBdeIzjG31J_7PvH23wLTMM&cs=200x200',
        name: 'John Doe',
        age: '29'
    };

    const getRandomAge = () => Math.floor(Math.random() * 31) + 20;

    const users = Array.from({ length: 50 }, (_, index) => ({
        ...baseUser,
        id: index + 1, // Добавляем уникальный id для каждого пользователя
        age: getRandomAge() // Добавляем случайный возраст
    }));

    const [selectedUser, setSelectedUser] = useState(users[0]);

    const handleUserClick = (user) => {
        console.log(`User clicked: ${user.name}`);
        setSelectedUser(user);
    };

    console.log(`Selected user ID in Home: ${selectedUser.id}`);

    return (
        <div className="content">
            <div className="grid">
                <div className="white_box tabled">
                    <div className="flex">
                        <UsersTable users={users} selectedUserId={selectedUser.id} onUserClick={handleUserClick} />
                    </div>
                </div>
                <div className="white_box profile_card overflowed">
                    <div className="header">
                        <h1>{selectedUser.name} {selectedUser.id}</h1>
                        <span>Registered at 8 June 2022</span>
                    </div>
                    <div className="flex">
                        <img src={selectedUser.photo} alt={`${selectedUser.name}'s photo`} />
                        <table>
                            <tbody>
                            <tr>
                                <td>Age:</td>
                                <td>{selectedUser.age}</td>
                            </tr>
                            <tr>
                                <td>Birth:</td>
                                <td>2 марта 1993 года</td>
                            </tr>
                            <tr>
                                <td>Email:</td>
                                <td>nikandtas@gmail.com</td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
