import React, { useState, useRef } from 'react';
import { useAuth } from '../context/AuthContext';

function AuthForm() {
    const { isAuthenticated, login, register, setIsAuthenticated } = useAuth();

    if (isAuthenticated) return <div>Вы уже авторизованы.</div>;

    const [isRegistering, setIsRegistering] = useState(false);
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        birthDate: '',
        gender: '',
        profilePicture: null,
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const fileInputRef = useRef(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleFileChange = (file) => {
        setFormData(prevState => ({
            ...prevState,
            profilePicture: file,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        // Создаем объект FormData
        const submitData = new FormData();
        submitData.append('email', formData.email);
        submitData.append('password', formData.password);
        submitData.append('birthDate', formData.birthDate);
        submitData.append('gender', formData.gender);
        if (formData.profilePicture) {
            submitData.append('profilePicture', formData.profilePicture);
        }

        // Отладка
        for (let [key, value] of submitData.entries()) {
            console.log(`${key}: ${value}`);
        }

        try {
            let response;
            if (isRegistering) {
                console.log('Registering...');
                response = await register(submitData);
            } else {
                console.log('Logging in...');
                response = await login(submitData);
            }

            if (response) {
                setSuccess('Успешно! Вход...');
            } else {
                throw new Error('Произошла ошибка, попробуйте снова');
            }
        } catch (err) {
            console.error('Error:', err);
            setError(err.message || 'Произошла ошибка, попробуйте снова');
        }
    };


    const toggleForm = () => {
        setIsRegistering(!isRegistering);
    };

    return (
        <div className="auth-form">
            <h2>{isRegistering ? 'Регистрация' : 'Войти'}</h2>
            {error && <span className="error">{error}</span>}
            {success && <span className="success">{success}</span>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        placeholder="name@example.com"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Пароль:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                {isRegistering && (
                    <>
                        <div>
                            <label>Дата рождения:</label>
                            <input
                                type="date"
                                name="birthDate"
                                value={formData.birthDate}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label>Ваш пол:</label>
                            <select
                                name="gender"
                                value={formData.gender}
                                onChange={handleChange}
                                required
                            >
                                <option value="" disabled>Выберите</option>
                                <option value="male">Мужчина</option>
                                <option value="female">Женщина</option>
                            </select>
                        </div>
                        <div
                            className="drop-zone"
                            onClick={() => fileInputRef.current.click()}
                        >
                            <label>Фотография:</label>
                            <input
                                type="file"
                                name="profilePicture"
                                ref={fileInputRef}
                                onChange={(e) => handleFileChange(e.target.files[0])}
                                accept="image/*"
                                style={{ display: 'none' }}
                            />
                            <p>
                                {formData.profilePicture ? (
                                    <img
                                        src={URL.createObjectURL(formData.profilePicture)}
                                        alt="Preview"
                                        style={{ maxWidth: '100%', maxHeight: '200px' }}
                                    />
                                ) : (
                                    "Перетащите сюда изображение или нажмите для выбора файла"
                                )}
                            </p>
                        </div>
                    </>
                )}
                <button type="submit">{isRegistering ? 'Регистрация' : 'Войти'}</button>
            </form>
            <p>
                {isRegistering ? (
                    <>Уже есть аккаунт? <a href="#" onClick={toggleForm}>Войти</a></>
                ) : (
                    <>Нет аккаунта? <a href="#" onClick={toggleForm}>Зарегистрируйтесь</a></>
                )}
            </p>
        </div>
    );
}

export default AuthForm;
