import React from 'react';
import { useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Импортируем хук для проверки авторизации

const RouteHandler = ({ children }) => {
    const location = useLocation();
    const { isAuthenticated } = useAuth(); // Получаем информацию об авторизации

    // Определяем текущий маршрут для добавления класса
    // Если пользователь не авторизован, устанавливаем класс 'login'
    const currentRoute = !isAuthenticated || location.pathname === '/login'
        ? 'login'
        : location.pathname.substring(1) || 'home';

    return (
        <div className={`app ${currentRoute}`}>
            {children}
        </div>
    );
};

export default RouteHandler;
