import React, { useState, useEffect } from 'react';

function Account() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        birthDate: '',
        gender: '',
        profilePicture: null,
    });

    useEffect(() => {
        // Замените этот URL на URL вашего API для получения данных пользователя
        const fetchUserData = async () => {
            try {
                const response = await fetch('/api/user');
                if (!response.ok) {
                    throw new Error('Не удалось получить данные пользователя');
                }
                const userData = await response.json();
                setFormData({
                    email: userData.email,
                    password: '', // Пароль обычно не передается с сервера для безопасности
                    birthDate: userData.birthDate,
                    gender: userData.gender,
                    profilePicture: userData.profilePicture,
                });
            } catch (error) {
                console.error('Ошибка получения данных пользователя:', error);
            }
        };

        fetchUserData();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (e) => {
        setFormData({
            ...formData,
            profilePicture: e.target.files[0],
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Profile Data:', formData);
        // Логика отправки данных профиля на сервер
    };

    return (
        <div className="content">
            <div className="white_box">
                <h2>Nikita, edit your profile</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            placeholder="name@example.com"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label>Birth Date:</label>
                        <input
                            type="date"
                            name="birthDate"
                            value={formData.birthDate}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label>Gender:</label>
                        <select
                            name="gender"
                            value={formData.gender}
                            onChange={handleChange}
                            required
                        >
                            <option value="" disabled>Select</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                        </select>
                    </div>
                    <div>
                        <label>Profile Picture:</label>
                        <input
                            type="file"
                            name="profilePicture"
                            onChange={handleFileChange}
                        />
                        {formData.profilePicture && (
                            <div>
                                <img
                                    src={URL.createObjectURL(formData.profilePicture)}
                                    alt="Profile"
                                    style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                                />
                                <p>Current Picture</p>
                            </div>
                        )}
                    </div>
                    <button type="submit">Save Changes</button>
                </form>
            </div>
        </div>
    );
}

export default Account;
