import React, { useState, useEffect } from 'react';
import UsersTable from '../components/UsersTable';
import '../assets/css/people.css';

// Функция для вычисления возраста
const calculateAge = (birthDate) => {
    const birth = new Date(birthDate);
    const today = new Date();
    let age = today.getFullYear() - birth.getFullYear();
    const monthDifference = today.getMonth() - birth.getMonth();
    if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < birth.getDate())) {
        age--;
    }
    return age;
};

function Home() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_URL}/users`);
                if (!response.ok) {
                    throw new Error('Не удалось получить пользователей');
                }
                const data = await response.json();
                setUsers(data);
                if (data.length > 0) {
                    setSelectedUser(data[0]); // Установить первого пользователя в качестве выбранного
                }
            } catch (error) {
                console.error('Ошибка получения данных пользователей:', error);
            }
        };

        fetchUsers();
    }, []);

    const handleUserClick = (user) => {
        console.log(`User clicked: ${user.name}`);
        setSelectedUser(user);
    };

    return (
        <div className="content">
            <div className="grid">
                <div className="white_box tabled">
                    <div className="flex">
                        <UsersTable users={users} selectedUserId={selectedUser ? selectedUser.id : null} onUserClick={handleUserClick} />
                    </div>
                </div>
                {selectedUser && (
                    <div className="white_box profile_card overflowed">
                        <div className="header">
                            <h1>{selectedUser.name} {selectedUser.id}</h1>
                            <span>Registered at 8 June 2022</span>
                        </div>
                        <div className="flex">
                            <img src={selectedUser.photo} alt={`${selectedUser.name}'s photo`} />
                            <table>
                                <tbody>
                                <tr>
                                    <td>Age:</td>
                                    <td>{selectedUser.age}</td>
                                </tr>
                                <tr>
                                    <td>Birth:</td>
                                    <td>2 марта 1993 года</td>
                                </tr>
                                <tr>
                                    <td>Email:</td>
                                    <td>{selectedUser.email}</td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Home;
