import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Home from './pages/Home';
import Account from './pages/Account';
import People from './pages/People';
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import Login from './pages/Login'; // Импортируем компонент логина
import Register from './pages/Register'; // Импортируем компонент регистрации

function App() {
    // Состояние для отслеживания авторизации
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Функция для обработки успешного входа
    const handleLogin = () => {
        setIsAuthenticated(true);
    };

    const currentRoute = location.pathname.substring(1) || 'home';

    return (
        <Router>
            <div className={`app ${currentRoute}`}>
                {!isAuthenticated ? (
                    <Switch>
                        <Route path="/login">
                            <Login onLogin={handleLogin}/>
                        </Route>
                        <Route path="/register" component={Register}/>
                        <Redirect to="/login"/>
                    </Switch>
                ) : (
                    <>
                        <Header/>
                        <div className="main-content">
                            <Sidebar/>
                            <div className="content">
                                <Switch>
                                    <Route exact path="/" component={Home}/>
                                    <Route path="/account" component={Account}/>
                                    <Route path="/people" component={People}/>
                                </Switch>
                            </div>
                        </div>
                    </>
                )}
            </div>
        </Router>
    );
}

export default App;
